        int x[50];
        int y[50];
        int len;
        int speed;
}snake;

//用于储存用户账号密码
typedef struct The_users
{
        char id[11]; //账号
        char pwd[20];//密码
}users;
users a, b;//新建结构体成员变量,用来登陆与注册


//此处声明变量

void init_map(void);
void update_food(void);
void move_snake(void);
