//分数系统
void score()
{
        fopen_s(&fp, "scores.dat", "r");
        for (n = 0; n < N - 1; n++)
        {
                fscanf_s(fp, "%d", &m);
                s[n] = m;
        }
        s[N - 1] = snake.len - 3;
        qsort(s, N, sizeof(s[0]), compDec);
        fclose(fp);
        fopen_s(&fp, "scores.dat", "w");
        for (n = 0; n < N - 1; n++)
        {
                fprintf_s(fp, "%d\n", s[n]);
        }
        fclose(fp);
}

//分数的排序
//升序排序
int compInc(const void *a, const void *b)
{
        return *(int *)a - *(int *)b;
}
//降序排序
int compDec(const void *a, const void *b) {
        return *(int *)b - *(int *)a;
}
